(function() {
    "use-strict";
    $(document).ready(function() {
        $(".disable-anchors a").click(function(a) {
            a.preventDefault();
        }), $("[data-href]").click(function(a) {
            var b, c, d, e;
            c = window.location.pathname.split("/"), b = c[c.length - 1], d = c[c.length - 2], 
            e = {}, e[d] = b, window.history.pushState(e, "", b), document.location.replace($(this).data("href"));
        });
    });
}).call(this);

/* BUTTON SEARCH FUNCTION*/

function enableordisable(){
  if($("#search_bar").val() !== ""){
    $("#search_button").removeAttr("disabled");
  }else{
    $("#search_button").attr("disabled", "disabled");
  }
}

